package com.rest.utility;

import java.awt.Image;
import java.awt.image.BufferedImage;
import java.awt.image.RenderedImage;
import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.IOException;
import java.util.Base64;
import java.util.Date;
import java.util.UUID;

import javax.imageio.ImageIO;

import org.apache.commons.lang.RandomStringUtils;

import sun.misc.BASE64Decoder;

public class ImageUtil {
	
	private static FileInputStream fileInputStreamReader;

	public static String encodeFileToBase64Binary(File file){
        String encodedfile = null;
        try {
            fileInputStreamReader = new FileInputStream(file);
            byte[] bytes = new byte[(int)file.length()];
            fileInputStreamReader.read(bytes);
            encodedfile = new String(Base64.getEncoder().encode(bytes), "UTF-8");
        } catch (FileNotFoundException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }

        return encodedfile;
    }
	
	public static void decodeFileToBase64Binary(String binarySourceData, String imageName, boolean thumbnailFlag) {		
		
		java.awt.Image image1 = null;			// create a buffered image
		byte[] imageByte      = null;

		/*
		 *   Sample Binay Data (base64)
		 *   
		 */
				
		BASE64Decoder decoder = new BASE64Decoder();
		try {
			imageByte = decoder.decodeBuffer(binarySourceData);
		} catch (IOException e) {
			e.printStackTrace();
		}
		ByteArrayInputStream bis = new ByteArrayInputStream(imageByte);
		try {
			image1 = ImageIO.read(bis);
		} catch (IOException e) {
			e.printStackTrace();
		}
		try {
			bis.close();
		} catch (IOException e) {
			e.printStackTrace();
		}

		// write the image to a file
		String filePath = ConstantsUtil.IMAGE_LOCATION + imageName + "." + ConstantsUtil.IMAGE_FORMAT; 
		
		File outputfile = new File(filePath);
		try {
			 ImageIO.write((RenderedImage) image1, ConstantsUtil.IMAGE_FORMAT, outputfile);
			 if(thumbnailFlag == true){
				 String thumbnailPath = ConstantsUtil.IMAGE_LOCATION + imageName + "icon." + ConstantsUtil.IMAGE_FORMAT;
				 saveScaledImage(filePath,thumbnailPath);
			 }
		} catch (IOException e) {
			e.printStackTrace();
		}	
	}
	
    public static String generateUniqueFileName() {
		
	    String filename = "";
	    long millis = System.currentTimeMillis();
	    String datetime = new Date().toGMTString();
	    datetime = datetime.replace(" ", "");
	    datetime = datetime.replace(":", "");
	    String rndchars = RandomStringUtils.randomAlphanumeric(16);
	    filename = rndchars + "_" + datetime + "_" + millis;
	    
        return UUID.nameUUIDFromBytes(filename.getBytes()).toString();
	}
    
    public static void saveScaledImage(String filePath,String outputFile){
        try {

            BufferedImage  sourceImage = ImageIO.read(new File(filePath));
            int width = sourceImage.getWidth();
            int height = sourceImage.getHeight();

            if(width>height){
                float extraSize=    height-100;
                float percentHight = (extraSize/height)*100;
                float percentWidth = width - ((width/100)*percentHight);
                BufferedImage img = new BufferedImage((int)percentWidth, 100, BufferedImage.TYPE_INT_RGB);
                Image scaledImage = sourceImage.getScaledInstance((int)percentWidth, 100, Image.SCALE_SMOOTH);
                img.createGraphics().drawImage(scaledImage, 0, 0, null);
                BufferedImage img2 = new BufferedImage(100, 100 ,BufferedImage.TYPE_INT_RGB);
                img2 = img.getSubimage((int)((percentWidth-100)/2), 0, 100, 100);

                ImageIO.write(img2, "jpg", new File(outputFile));    
            }else{
                float extraSize=    width-100;
                float percentWidth = (extraSize/width)*100;
                float  percentHight = height - ((height/100)*percentWidth);
                BufferedImage img = new BufferedImage(100, (int)percentHight, BufferedImage.TYPE_INT_RGB);
                Image scaledImage = sourceImage.getScaledInstance(100,(int)percentHight, Image.SCALE_SMOOTH);
                img.createGraphics().drawImage(scaledImage, 0, 0, null);
                BufferedImage img2 = new BufferedImage(100, 100 ,BufferedImage.TYPE_INT_RGB);
                img2 = img.getSubimage(0, (int)((percentHight-100)/2), 100, 100);

                ImageIO.write(img2, "jpg", new File(outputFile));
            }

        } catch (IOException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }
    }
    
    public static boolean deleteFile(String filename){
    	    	 
    	  File file = new File(filename); 
    	  boolean success = false;      
          success = file.delete();
          if (success) {
              System.out.println(file.getAbsoluteFile() + " Deleted");
          } else {
              System.out.println(file.getAbsoluteFile() + " Deletion failed!!!");
          }
    	  return success;
    }    
   
}